package Day3;

	


public class FarenhitetoCelsious {
	public static void FarenhiteConvertCelsious(double f) {
		double c;
		c =(f-32)*5/9;
		System.out.println("converted value in C:" + c);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		FarenhiteConvertCelsious(120);
	}

}
